       chargement jo = new chargement();
        jo.setVisible(true);
        for (int i=0; i<=100; i++) {
            Thread.sleep(30);
        jo.bar.setValue(i);
        //Ajout des noms:
        if(i==0){
            jo.txt.setText("Nom du groupe");
        }
        else if(i==15){
            jo.txt.setText("Avec la participation de :");
        }
        else if(i==35){
            jo.txt.setText("Chef du goupe: Nom");
        }
        else if(i==55){
            jo.txt.setText("Adjoint: Nom");
        }
        else if(i==75){
            jo.txt.setText("Conseilliere: Nom");
        }
        else if(i==85){
            jo.txt.setText("Sous la responsabilite");
        }
        else if(i==95){
            jo.txt.setText("Du doyen MUSANGU");
        }
        }
        
        jo.setVisible(false);
